import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Col } from 'react-flexbox-grid'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import RecipeScore from './RecipeScore'
import average from '../../services/operations'
import ReactStarRating from "react-star-ratings-component";
import './styles.css'

class Recipe extends Component {

    constructor(props){
        super(props)
        const {name, ingredients, imageURL, steps, calification, _id } = props.recipe

        //const calificationAVG = average()

        this.state = {
            showModal: false,
            id: _id,
            name,
            ingredients,
            imageURL,
            steps,
            calification: average(calification)
        }

    }

    handleModal(showModal){
        this.setState( {showModal })
    }

    render() {
        const {imageURL, name, ingredients, showModal, calification, steps, id} = this.state
        return (
            <>
                <Col xs={12} sm={4}>
                    <Card>
                        <Card.Img variant="top" src={imageURL} />
                        <Card.Body>
                            <Card.Title>{ name }</Card.Title>
                            <Card.Text>To make this recipe you will need:</Card.Text>
                            <Card.Body> 
                                    <ul>
                                        {/* Improve key */}
                                        { ingredients.map((recipe) => <li key={recipe.name + recipe.quantity}>{`${recipe.quantity} ${recipe.unit} of ${recipe.name}`}</li>)}
                                    </ul>
                            </Card.Body>
                            <Button variant="primary" className="mr-3" onClick={() => {this.handleModal(true)}}>See Steps</Button>
                            <ReactStarRating
                                numberOfStar={5}
                                numberOfSelectedStar={calification}
                                colorEmptyStar="#AAA"
                                starSize="35px"
                                spaceBetweenStar="10px"
                                />
                        </Card.Body>
                    </Card>
                </Col>



                {/* Modal */}
                <Modal show={showModal} size="lg">
                    <Modal.Header>
                        <h2>{name}</h2>
                    </Modal.Header>
                    <Modal.Body>
                        <p>Steps:</p>
                        <p>{steps}</p>
                        <hr/>
                        <p>Did you like this recipe? <b>Rate it!</b></p>
                        <RecipeScore id={id} onClick={this.handleModal}></RecipeScore>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="primary" className="mr-3" onClick={() => {this.handleModal(false)}}>Close</Button>
                    </Modal.Footer>
                </Modal>
                {/* End modal */}
            </>
        );
    }
}

Recipe.propTypes = {

};

export default Recipe;
