import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Col } from 'react-flexbox-grid'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import Star from '@material-ui/icons/Star'
import StarHalf from '@material-ui/icons/StarHalf'
import StarBorder from '@material-ui/icons/StarBorder'
import './styles.css'

class Recipe extends Component {
// const Recipe = ({data: { temperature, weatherState, humidity, wind }}) => {

    constructor(props){
        super(props)
        const {name, ingredients, imageURL } = props.recipe

        this.state = {
            showModal: false,
            name,
            ingredients
        }
        
        
        console.log(this.props.imageURL)

    }

    handleModal(showModal){
        this.setState( {showModal })
    }

    render() {
        return (
            <>
                <Col xs={12} sm={4}>
                    <Card>
                        <Card.Img variant="top" src={this.props.imageURL} />
                        <Card.Body>
                            <Card.Title>{ this.state.name }</Card.Title>
                            <Card.Text>To make this recipe you will need:</Card.Text>
                            <Card.Body> 
                                    <ul>
                                        { this.state.ingredients.map((recipe) => <li key={recipe.name}>{`${recipe.quantity} ${recipe.unit} of ${recipe.name}`}</li>)}
                                    </ul>
                            </Card.Body>
                            <Button variant="primary" className="mr-3" onClick={() => {this.handleModal(true)}}>See Steps</Button>
                            
                            <Star className="star"></Star>
                            {/* <Star className="star"></Star>
                            <StarHalf className="star"></StarHalf> 
                            <StarBorder className="star"></StarBorder>
                            <StarBorder className="star"></StarBorder> */}
                        </Card.Body>
                    </Card>
                </Col>



                {/* Modal */}
                <Modal show={this.state.showModal}>
                    <Modal.Header>
                        {this.state.title}
                    </Modal.Header>
                    <Modal.Body>
                        {this.state.steps}
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="primary" className="mr-3" onClick={() => {this.handleModal(false)}}>Close</Button>
                    </Modal.Footer>
                </Modal>
                {/* End modal */}
            </>
        );
    }
}

Recipe.propTypes = {

};

export default Recipe;
