import React, { Component } from 'react';
import CircularProgress from '@material-ui/core/CircularProgress'
import Recipe from '../Recipe'
import PropTypes from 'prop-types';
import getRecipes from './../../services/getRecipes'
import './styles.css'


class RecipeList extends Component {
    constructor(props){
        super(props)

        const {textToSearch, data} = props
        
        this.state = {
            textToSearch,
            data
        }
        
    }

    searchRecipes = (textToSearch) => {
        fetch(getRecipes(textToSearch)).then( resolve => {
            return resolve.json()
        }).then(data => {
            this.setState({
                data
            })
        })
    }

    componentDidMount(){
        //Only first time
        this.searchRecipes(this.state.textToSearch)
    }

    render() {
        
        const {data} = this.state

        return (
            <>
                { data ? data.recipes.map((recipe) => ( <Recipe recipe={recipe} key={recipe.name}></Recipe> )) : <CircularProgress size={80} /> }
            </>
        );
    }
}

 RecipeList.propTypes = {
     textToSearch: PropTypes.string.isRequired,
 };

export default RecipeList;
