import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Col } from 'react-flexbox-grid'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button'
import ListGroup from 'react-bootstrap/ListGroup'
//import StarIcon from '@material-ui/icons/Star';

class RecipeList extends Component {
    constructor(){
        super()
        this.state = {
            title: "Título",
            steps: "Some quick example text to build on the card title and make up the bulk of the card's content."
        }
    }

    render() {
        return (
            <Col xs={12} sm={4}>
                <Card>
                    <Card.Img variant="top" src="https://www.helpguide.org/wp-content/uploads/table-with-grains-vegetables-fruit-768.jpg" />
                    {/* <ListGroup variant="flush">
                        <ListGroup.Item>Cras justo odio</ListGroup.Item>
                        <ListGroup.Item>Dapibus ac facilisis in</ListGroup.Item>
                        <ListGroup.Item>Vestibulum at eros</ListGroup.Item>
                    </ListGroup> */}
                    <Card.Body>
                        <Card.Title>{ this.state.title }</Card.Title>
                        <Card.Text> { this.state.steps } </Card.Text>
                        <Button variant="primary">See More</Button>
                        {/* <StarIcon></StarIcon> */}
                    </Card.Body>
                </Card>
            </Col>
        );
    }
}

RecipeList.propTypes = {

};

export default RecipeList;
