import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Col } from 'react-flexbox-grid'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import Star from '@material-ui/icons/Star'
import StarHalf from '@material-ui/icons/StarHalf'
import StarBorder from '@material-ui/icons/StarBorder'
import RecipeScore from './RecipeScore'
import './styles.css'

class Recipe extends Component {

    constructor(props){
        super(props)
        const {name, ingredients, imageURL, steps, calification } = props.recipe

        this.state = {
            showModal: false,
            name,
            ingredients,
            imageURL,
            steps,
            calification
        }

    }

    handleModal(showModal){
        this.setState( {showModal })
    }

    render() {
        return (
            <>
                <Col xs={12} sm={4}>
                    <Card>
                        <Card.Img variant="top" src={this.state.imageURL} />
                        <Card.Body>
                            <Card.Title>{ this.state.name }</Card.Title>
                            <Card.Text>To make this recipe you will need:</Card.Text>
                            <Card.Body> 
                                    <ul>
                                        { this.state.ingredients.map((recipe) => <li key={recipe.name + recipe.quantity}>{`${recipe.quantity} ${recipe.unit} of ${recipe.name}`}</li>)}
                                    </ul>
                            </Card.Body>
                            <Button variant="primary" className="mr-3" onClick={() => {this.handleModal(true)}}>See Steps</Button>
                            
                            {}
                            {
                                this.state.calification.reduce((a,b) => a + b, 0) / arr.length
                            }
                            <Star className="star"></Star>
                            <Star className="star"></Star>
                            <StarHalf className="star"></StarHalf> 
                            <StarBorder className="star"></StarBorder>
                            <StarBorder className="star"></StarBorder>
                        </Card.Body>
                    </Card>
                </Col>



                {/* Modal */}
                <Modal show={this.state.showModal} size="lg">
                    <Modal.Header>
                        <h2>{this.state.name}</h2>
                    </Modal.Header>
                    <Modal.Body>
                        <p>Steps:</p>
                        {this.state.steps}
                        <hr/>
                        <p>Did you like the recipe? <b>Let us know!</b></p>
                        <RecipeScore></RecipeScore>
                    </Modal.Body>
                    <Modal.Footer>
                        <Button variant="primary" className="mr-3" onClick={() => {this.handleModal(false)}}>Close</Button>
                    </Modal.Footer>
                </Modal>
                {/* End modal */}
            </>
        );
    }
}

Recipe.propTypes = {

};

export default Recipe;
