import getRecipes from './../../services/getRecipes'
import './styles.css'

class RecipeList extends Component {
    constructor(props){
        super(props)
        console.log("props", props)
        
        const {data} = props
        this.state = {
            data
        }
    }

    //componentDidMount() {
