import React, { Component } from 'react';
import RecipeList from '../RecipeList'
import RecipeSearch from '../RecipeSearch'
import { Col, Row} from 'react-flexbox-grid'
import PropTypes from 'prop-types';

class Recipes extends Component {
    constructor(props){
        super(props)
        this.state = {
            textToSearch: ''
        }
    }

    handleSearch = (e) => {
        this.setState({textToSearch: e.target.value})
    }
    
    render() {
        return (
            <>
                <Row>
                    <Col xs={12}>
                    <RecipeSearch handleSearch={this.handleSearch}></RecipeSearch>
                    </Col>
                </Row>
                <hr />
                <Row>
                    <Col xs={12}>
                        <h2 className="mb-3 mt-4">List of recipes</h2>
                    </Col>
                </Row>
                <Row>
                    {/* {console.log("before list", this.state.textToSearch)} */}
                    <h2>{textToSearch}</h2>
                    <RecipeList textToSearch={this.state.textToSearch}></RecipeList>
                </Row>
            </>
        );
    }
}

Recipes.propTypes = {
};

export default Recipes;
