import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Col } from 'react-flexbox-grid'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import Star from '@material-ui/icons/Star'
import StarHalf from '@material-ui/icons/StarHalf'
import StarBorder from '@material-ui/icons/StarBorder'
import './styles.css'

class RecipeList extends Component {
    constructor(){
        super()
        this.state = {
            title: "Title",
            steps: "Some quick example text to build on the card title and make up the bulk of the card's content."
        }
    }
    


    render() {

        return (
            <>
                <Col xs={12} sm={4}>
                    <Card>
                        <Card.Img variant="top" src="https://www.helpguide.org/wp-content/uploads/table-with-grains-vegetables-fruit-768.jpg" />
                        <Card.Body>
                            <Card.Title>{ this.state.title }</Card.Title>
                            <Card.Text> { this.state.steps } </Card.Text>
                            <Button variant="primary" className="mr-3">See More</Button>
                            
                            <Star className="star"></Star>
                            <Star className="star"></Star>
                            <StarHalf className="star"></StarHalf> 
                            <StarBorder className="star"></StarBorder>
                            <StarBorder className="star"></StarBorder>
                        </Card.Body>
                    </Card>
                </Col>
                <Modal show={false}>

                </Modal>
            </>
        );
    }
}

RecipeList.propTypes = {

};

export default RecipeList;
