import React, { Component } from 'react';
import CircularProgress from '@material-ui/core/CircularProgress'
import Recipe from './Recipe'
import PropTypes from 'prop-types';
import getRecipes from './../../services/getRecipes'
import './styles.css'

class RecipeList extends Component {
    constructor(props){
        super(props)

        const {textToSearch} = props

        this.state = {
            textToSearch,
            data: null
        }
    }

    searchRecipes = (textToSearch) => {
        console.log("log", textToSearch)
         fetch(`${getRecipes()}/${textToSearch}`).then( resolve => {
             return resolve.json()
         }).then(data => {
              this.setState({
                  data
              })
         })
    }

    componentDidUpdate(){
        this.searchRecipes(this.props.textToSearch)
    }

    render() {
        
        // const {data} = this.state
        
        // if (data) {
        //     let recipes = data.recipes.map((recipe) => recipe)
        //     console.log(recipes)
        // }

        return (
            <>
            <h1>{this.props.textToSearch}</h1>
                {/* { data ? data.recipes.map((recipe) => ( <Recipe recipe={recipe} key={recipe.name}></Recipe> )) : <CircularProgress size={80} /> } */}
            </>
        );
    }
}

RecipeList.propTypes = {
    textToSearch: PropTypes.string.isRequired,
};

export default RecipeList;
