import React, { Component } from 'react';
import RecipeList from '../RecipeList'
import RecipeSearch from '../RecipeSearch'
import { Col, Row} from 'react-flexbox-grid'
import getRecipes from './../../services/getRecipes'
import RecipeSearchButtons from './'
import PropTypes from 'prop-types';

class Recipes extends Component {
    constructor(props){
        super(props)
        this.state = {
            data: {}
        }
    }

    handleSearch = (e) => {
        fetch(getRecipes(e.target.value)).then( resolve => {
            return resolve.json()
        }).then(data => {
            console.log("Lo que trae", data)
            this.setState({
                data
            })
        })
    }

    componentDidMount(){
        fetch(getRecipes()).then( resolve => {
            return resolve.json()
        }).then(data => {
            console.log("Lo que trae did mount", data)
            this.setState({
                data
            })
        })
    }

    render() {
        const { data } = this.state
        console.log("Data en el render", data)
        return (
            <>
                <Row>
                    <Col md={8}>
                        <RecipeSearch handleSearch={this.handleSearch}></RecipeSearch>
                    </Col>
                    <Col md={4}>
                        <RecipeSearchButtons></RecipeSearchButtons>
                    </Col>
                </Row>
                <hr />
                <Row>
                    <Col xs={12}>
                        <h2 className="mb-3 mt-4">List of recipes</h2>
                    </Col>
                </Row>
                <Row>
                    {/* <RecipeList textToSearch={''}></RecipeList> */}
                    <RecipeList data={data}></RecipeList>
                </Row>
            </>
        );
    }
}

Recipes.propTypes = {
};

export default Recipes;
