import React, { Component } from 'react';
import RecipeList from '../RecipeList'
import RecipeSearch from '../RecipeSearch'
import { Col, Row} from 'react-flexbox-grid'
import getRecipes from './../../services/getRecipes'
import PropTypes from 'prop-types';

class Recipes extends Component {
    constructor(props){
        super(props)
        this.state = {
            data: props.data
        }
    }

    handleSearch = (e) => {
        console.log(e.target.value)
        //this.setState({state: e.target.value})
    //   fetch(`${getRecipes()}/${e.target.value}`).then( resolve => {
    //         return resolve.json()
    //     }).then(data => {
    //         this.setState({
    //             data
    //         })
    //     })
    }

    
    render() {
        return (
            <>
                <Row>
                    <Col xs={12}>
                        <RecipeSearch handleSearch={this.handleSearch}></RecipeSearch>
                    </Col>
                </Row>
                <hr />
                <Row>
                    <Col xs={12}>
                        <h2 className="mb-3 mt-4">List of recipes</h2>
                    </Col>
                </Row>
                <Row>
                    {console.log("la data que se le pasa" , this.state.data)}
                    <RecipeList data={this.state.data}></RecipeList>
                </Row>
            </>
        );
    }
}

Recipes.propTypes = {
};

export default Recipes;
