import React, { Component } from 'react';
import RecipeList from '../RecipeList'
import RecipeSearch from '../RecipeSearch'
import { Col, Row} from 'react-flexbox-grid'
import getRecipes from './../../services/getRecipes'
import PropTypes from 'prop-types';

class Recipes extends Component {
    constructor(props){
        super(props)
        this.state = {
            data: null
        }
    }

    handleSearch = (e) => {
        console.log(e.target.value)
        // fetch(getRecipes(this.state.textToSearch)).then( resolve => {
        //     return resolve.json()
        // }).then(data => {
        //     console.log(data)
        //     this.setState({
        //         data: data
        //     })
        // })
    }

    render() {
        const { data } = this.state

        return (
            <>
                <Row>
                    <Col xs={12}>
                        <RecipeSearch handleSearch={this.handleSearch}></RecipeSearch>
                    </Col>
                </Row>
                <hr />
                <Row>
                    <Col xs={12}>
                        <h2 className="mb-3 mt-4">List of recipes</h2>
                    </Col>
                </Row>
                <Row>
                    {/* <RecipeList textToSearch={''}></RecipeList> */}
                    <RecipeList data={data}></RecipeList>
                </Row>
            </>
        );
    }
}

Recipes.propTypes = {
};

export default Recipes;
