import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Col } from 'react-flexbox-grid'
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import StarIcon from '@material-ui/icons/Star';

class RecipeList extends Component {
    constructor(){
        super()
        this.state = {
            title: "Título",
            steps: "Some quick example text to build on the card title and make up the bulk of the card's content."
        }
    }
    


    render() {

        const show = false

        return (
            <Col xs={12} sm={4}>
                <Card>
                    <Card.Img variant="top" src="https://www.helpguide.org/wp-content/uploads/table-with-grains-vegetables-fruit-768.jpg" />
                    <Card.Body>
                        <Card.Title>{ this.state.title }</Card.Title>
                        <Card.Text> { this.state.steps } </Card.Text>
                        <Button variant="primary" onClick={() => { show }}>See More</Button>
                        <StarIcon></StarIcon> 
                    </Card.Body>
                </Card>
            </Col>
        );
    }
}

RecipeList.propTypes = {

};

export default RecipeList;
