import React from 'react';
import Card from 'react-bootstrap/Card';
import PropTypes from 'prop-types';

const Ingredients = (props) => {
    
    let handleChange = (event) => {
        this.setState({value: event.target.value});
    }

    return (
        <div>

            <Card.Body> 
            <Card.Text>
                Servings:
                <input type="text" className="ml-2" value={this.state.value} onChange={this.handleChange} />
            </Card.Text>
            <Card.Text>To make this recipe you will need:</Card.Text>
                <ul>
                    {/* Improve key */}
                    { props.ingredients.map((recipe) => <li key={recipe.name + recipe.quantity}>{`${recipe.quantity} ${recipe.unit} of ${recipe.name}`}</li>)}
                </ul>
            </Card.Body>
        </div>
    );
}

Ingredients.propTypes = {
    
};


export default Ingredients;
