import React, { Component } from 'react';
import CircularProgress from '@material-ui/core/CircularProgress'
import Recipe from './Recipe'
import getRecipes from './../../services/getRecipes'
import './styles.css'

class RecipeList extends Component {
    constructor(props){
        super(props)
        this.state = {
            data: null
        }
    }

    componentDidMount() {
        this.handleUpdateClick()
    }
    
    handleUpdateClick = () => {
        fetch(getRecipes()).then( resolve => {
            return resolve.json()
        }).then(data => {
            this.setState({
                data
            })
        })
    }

    handleModal(showModal){
        this.setState( {showModal })
    }

    render() {
        const {data} = this.state
        if (data) 
            console.log(data.recipes.map((recipe) => recipe))
        return (
            <>
                {/* { data ? <Recipe title={"asdsadssdaasd"} ></Recipe> : <CircularProgress size={80} /> } */}
                { data ? data.recipes.map((recipe) => { <div> {recipe} </div> }) : <CircularProgress size={80} /> }
            </>
        );
    }
}

RecipeList.propTypes = {

};

export default RecipeList;
