import React, { Component } from 'react';
import CircularProgress from '@material-ui/core/CircularProgress'
import Recipe from './Recipe'
import getRecipes from './../../services/getRecipes'
import './styles.css'

class RecipeList extends Component {
    constructor(props){
        super(props)
        this.state = {
            data: null
        }
    }

    componentDidMount() {
        this.handleUpdateClick()
    }
    
    handleUpdateClick = () => {
        fetch(getRecipes()).then( resolve => {
            return resolve.json()
        }).then(data => {
            this.setState({
                data
            })
        })
    }

    render() {
        const {data} = this.state
        
        if (data) {
            let recipes = data.recipes.map((recipe) => recipe)
            console.log(recipes)
        }

        return (
            <>
                { data ? data.recipes.map((recipe) => ( <Recipe recipe={recipe} key={recipe.name}></Recipe> )) : <CircularProgress size={80} /> }
            </>
        );
    }
}

RecipeList.propTypes = {

};

export default RecipeList;
