import { Col, Row} from 'react-flexbox-grid'
import getRecipes from './../../services/getRecipes'
import PropTypes from 'prop-types';

const laData = { nombre: "mati", edad:"26"}

class Recipes extends Component {
    constructor(props){
        super(props)
        this.state = {
            textToSearch: '',
            data: null
        }
    }

    handleSearch = (e) => {
        this.setState({textToSearch: e.target.value})

        fetch(getRecipes(this.state.textToSearch)).then( resolve => {
            return resolve.json()
        }).then(data => {
             this.setState({
                 data
             })
        })
    }
    
