        //          data
        //      })
        //  })
    }     
    
    // componentDidMount(){
    //     this.searchRecipes(this.state.textToSearch)
    // }

    render() {
        const { data } = this.state
        return (
            <>
                <Row>
                    <Col xs={12}>
                    <RecipeSearch handleSearch={this.handleSearch}></RecipeSearch>
                    </Col>
                </Row>
                <hr />
                <Row>
                    <Col xs={12}>
                        <h2 className="mb-3 mt-4">List of recipes</h2>
                    </Col>
                </Row>
                <Row>
                    <RecipeList laData={laData}></RecipeList>
                </Row>
            </>
        );
    }
}

Recipes.propTypes = {
};

export default Recipes;
