import React from 'react';
import Card from 'react-bootstrap/Card';
import PropTypes from 'prop-types';

const servings = 1

const handleChange = (event) => {
    servings = event.target.value;
    console.log(servings)
}

const Ingredients = (props) => {
    
    return (
        <div>
            <Card.Body> 
            <Card.Text>
                Servings:
                <input type="number" className="ml-2" onChange={handleChange}/>
            </Card.Text>
            <Card.Text>To make this recipe you will need:</Card.Text>
                <ul>
                    {/* Improve key */}
                    { props.ingredients.map((recipe) => <li key={recipe.name + recipe.quantity}>{`${recipe.quantity} ${recipe.unit} of ${recipe.name}`}</li>)}
                </ul>
            </Card.Body>
        </div>
    );
}

Ingredients.propTypes = {
    
};


export default Ingredients;
