import React, { Component } from 'react';
import Card from 'react-bootstrap/Card';
import PropTypes from 'prop-types';

class Ingredients extends Component {
    constructor(props){
        super(props)
        const { servings, ingredients } = this.props
        this.state = {
            servings,
            ingredients
        }
    }

    handleChange = (event) => {
        console.log(event.target.value)
        // this.setState = {
        //     servings: event.target.value
        // } 
    }
    
    render() {
        return (
            <div>
                <Card.Text>
                    Servings:
                    <input type="number" className="ml-2" onChange={this.handleChange}/>
                </Card.Text>
                <Card.Body> 
                <Card.Text>To make this recipe you will need:</Card.Text>
                    <ul>
                        {/* Improve key */}
                        { this.state.ingredients.map((recipe) => <li key={recipe.name + recipe.quantity}>{`${recipe.quantity * this.state.servings} ${recipe.unit} of ${recipe.name}`}</li>)}
                    </ul>
                </Card.Body>
            </div>
        );
    }
}

Ingredients.propTypes = {

};

export default Ingredients;
